// Get the current time for the backup
const now = new Date().toISOString();
const timestamp = now.replace(/[:.]/g, '-');

// Collect all data from different nodes
const folders = $('Get Folders').all();
const lists = $('Get Lists').all();
const sprints = $('Get Sprints').all();

// Create Google Doc content
const docContent = `# ClickUp Backup Report

**Backup Date:** ${new Date(now).toLocaleString()}
**Space ID:** 90153332732
**Team ID:** 42092860

---

## ðŸ“‚ Folders (${folders.length > 0 ? folders[0].json.folders?.length || 0 : 0})

${folders.length > 0 && folders[0].json.folders ? folders[0].json.folders.map(folder => `
### ${folder.name}
- **ID:** ${folder.id}
- **Private:** ${folder.private ? 'Yes' : 'No'}
- **Archived:** ${folder.archived ? 'Yes' : 'No'}
- **Status:** ${folder.status?.status || 'N/A'}
- **Order Index:** ${folder.orderindex}
`).join('\n') : 'No folders found'}

---

## ðŸ“‹ Lists (${lists.length > 0 ? lists[0].json.lists?.length || 0 : 0})

${lists.length > 0 && lists[0].json.lists ? lists[0].json.lists.map(list => `
### ${list.name}
- **ID:** ${list.id}
- **Private:** ${list.private ? 'Yes' : 'No'}
- **Archived:** ${list.archived ? 'Yes' : 'No'}
- **Status:** ${list.status?.status || 'N/A'}
- **Order Index:** ${list.orderindex}
- **Folder ID:** ${list.folder?.id || 'N/A'}
- **Folder Name:** ${list.folder?.name || 'N/A'}
`).join('\n') : 'No lists found'}

---

## ðŸŽ¯ Sprints (${sprints.length > 0 ? sprints[0].json.sprints?.length || 0 : 0})

${sprints.length > 0 && sprints[0].json.sprints ? sprints[0].json.sprints.map(sprint => `
### ${sprint.name}
- **ID:** ${sprint.id}
- **Status:** ${sprint.status || 'N/A'}
- **Start Date:** ${sprint.start_date ? new Date(sprint.start_date).toLocaleDateString() : 'N/A'}
- **End Date:** ${sprint.end_date ? new Date(sprint.end_date).toLocaleDateString() : 'N/A'}
- **Created:** ${sprint.date_created ? new Date(sprint.date_created).toLocaleDateString() : 'N/A'}
`).join('\n') : 'No sprints found'}

---

## ðŸ“Š Backup Statistics

- **Total Folders:** ${folders.length > 0 ? folders[0].json.folders?.length || 0 : 0}
- **Total Lists:** ${lists.length > 0 ? lists[0].json.lists?.length || 0 : 0}
- **Total Sprints:** ${sprints.length > 0 ? sprints[0].json.sprints?.length || 0 : 0}
- **Backup Generated:** ${new Date(now).toLocaleString()}

---

*This backup was generated automatically by the ClickUp Backup Agent.*`;

// Create filename
const filename = `ClickUp Backup - ${timestamp}`;

return {
  json: {
    filename: filename,
    content: docContent,
    mimeType: 'application/vnd.google-apps.document'
  }
};
